import { useState } from "react";
import MapView from "./components/MapView";
import Dashboard from "./components/Dashboard";
import Filters from "./components/Filters";

export default function App() {
  const [data, setData] = useState([]);
  const [filtered, setFiltered] = useState([]);
  const [activeTab, setActiveTab] = useState("map"); // üëà controla el tab activo

  // cargar datos (lo mismo que antes)
  useState(() => {
    fetch("/data/mock.json")
      .then((res) => res.json())
      .then((json) => {
        setData(json);
        setFiltered(json);
      });
  }, []);

  const handleFilter = (municipio) => {
    if (!municipio) setFiltered(data);
    else setFiltered(data.filter((d) => d.municipio === municipio));
  };

  return (
    <div className="container mx-auto p-4">
      <h1 className="text-2xl font-bold mb-4">Pueba Nasa Space Apps</h1>

      {/* Filtros arriba (opcional) */}
      <Filters onChange={handleFilter} />

      {/* Barra de tabs */}
      <div className="flex gap-4 mb-4">
        <button
          className={`p-2 border-b-2 ${
            activeTab === "map" ? "border-blue-500 font-bold" : "border-transparent"
          }`}
          onClick={() => setActiveTab("map")}
        >
          üåç Mapa
        </button>
        <button
          className={`p-2 border-b-2 ${
            activeTab === "dashboard" ? "border-blue-500 font-bold" : "border-transparent"
          }`}
          onClick={() => setActiveTab("dashboard")}
        >
          Dashboard
        </button>
      </div>

      {/* Contenido de cada tab */}
      {activeTab === "map" && <MapView data={filtered} />}
      {activeTab === "dashboard" && <Dashboard data={filtered} />}
    </div>
  );
}
