import { useState, useEffect } from 'react';
import MapView from './components/MapView';
import Dashboard from './components/Dashboard';
import Filters from './components/Filters';

export default function App() {
  const [data, setData] = useState([]);
  const [filtered, setFiltered] = useState([]);

  useEffect(() => {
    fetch('/public/data/mock.json')
      .then(res => res.json())
      .then(json => {
        setData(json);
        setFiltered(json);
      });
  }, []);

  const handleFilter = (municipio) => {
    if (!municipio) setFiltered(data);
    else setFiltered(data.filter(d => d.municipio === municipio));
  };

  return (
    <div className="container mx-auto p-4">
      <h1 className="text-2xl font-bold mb-4">NASA Space Apps Dashboard</h1>
      <Filters onChange={handleFilter} />
      <MapView data={filtered} />
      <Dashboard data={filtered} />
    </div>
  );
}
