import { Bar } from 'react-chartjs-2';
import 'chart.js/auto';

export default function Dashboard({ data }) {
  if (!data || !Array.isArray(data) || data.length === 0) {
    return <div className="my-4">No hay datos disponibles para mostrar.</div>;
  }

  // Filtramos valores válidos: solo números
  const labels = [];
  const pm25 = [];
  const ozono = [];

  data.forEach(d => {
    if (d.municipio && typeof d.pm25 === 'number' && typeof d.ozono === 'number') {
      labels.push(d.municipio);
      pm25.push(d.pm25);
      ozono.push(d.ozono);
    }
  });

  if (labels.length === 0) {
    return <div className="my-4">No hay datos válidos para mostrar.</div>;
  }

  const chartData = {
    labels,
    datasets: [
      { label: 'PM2.5', data: pm25, backgroundColor: 'rgba(255,99,132,0.5)' },
      { label: 'Ozono', data: ozono, backgroundColor: 'rgba(54,162,235,0.5)' }
    ]
  };

  return (
    <div className="my-4">
      <Bar data={chartData} />
    </div>
  );
}
